import {createTodo, readTodos, updateTodo, deleteTodo } from "../api/crud"
//todolist function

const TodoList = (props) => {
    return (
        <ul className="list-group">
            {props.todos.map(todo => {
                return <TodoListItem todo={todo} key={todo.id} deleteTodoItem={props.deleteTodoItem}/>
            })}
        </ul>
    )
}

//list item
const TodoListItem = (props) => {
    return (
        <li className="list-group-item d-flex justify-content-between align-items-center">
            {props.todo.title}
            <span className="badge bg-primary rounded-pill" onClick={() => props.deleteTodoItem(props.todo.id)}>X</span>
        </li>
    )
}

//generate todolist 
class TodoListContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: []
        }
    }

    //get data from api
    componentDidMount() {
        this.refreshData();
    }

    //refresh data
    refreshData = () => {
        readTodos().then(data => {
            this.setState({
                todos: [...data]
            })
        })
    }

    //delete todo item
    deleteTodoItem = (id) => {
        deleteTodo(id).then(() => {
            this.refreshData();
        })
    }

    //create todo item
    createTodoItem = (data) => {
        createTodo({
            title: data
        }).then(() => {
            this.refreshData();
        })
    }

    //update todo item
    updateTodoItem = (id, data) => {
        updateTodo(id, {
            title: data
        }).then(() => {
            this.refreshData();
        })
    }
    

    render() {
        return (
            <TodoList todos={this.state.todos} deleteTodoItem={this.deleteTodoItem}/>
        )
    }
}

export default TodoListContainer;
