import {createTodo, readTodos, updateTodo, deleteTodo } from "../api/crud"
import React, { Component } from 'react'
//todolist function

const TodoList = (props) => {
    return (
        <ul className="list-group">
            {props.todos.map(todo => {
                return <TodoListItem todo={todo} key={todo.id} deleteTodoItem={props.deleteTodoItem}/>
            })}
        </ul>
    )
}

//list item
const TodoListItem = (props) => {
    return (
        <li className="grid">
            {props.todo.title}
            <span className="button rounded" onClick={() => props.deleteTodoItem(props.todo.id)}>X</span>
        </li>
    )
}


//generate todolist 
export class TodoListContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todos: []
        }

        //log this
        console.log("Start");   
    }

    //get data from api
    componentDidMount() {
        refreshData();
    }

    //refresh data
    static refreshData = () => {
        readTodos().then(data => {
            this.setState({
                todos: [...data]
            })

            //log data
            console.log(data);
        })
    }

    //delete todo item
    deleteTodoItem = (id) => {
        deleteTodo(id).then(() => {
            this.refreshData();
        })
    }

    //create todo item
    createTodoItem = (data) => {
        createTodo({
            title: data
        }).then(() => {
            this.refreshData();
        })
    }

    //update todo item
    updateTodoItem = (id, data) => {
        updateTodo(id, {
            title: data
        }).then(() => {
            TodoListContainer.refreshData();
        })
    }
    

    render() {
        return (
            <TodoList todos={this.state.todos} deleteTodoItem={this.deleteTodoItem}/>
        )
    }
}

//create todo item class
export class CreateTodoItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "",
            description: ""
        }
    }

    //handle change
    handleChange = (event) => {
        this.setState({
            [event.target.id]: event.target.value
        })

        //log event
        console.log(event.target.id);
    }


    //handle submit
    handleSubmit = (event) => {
        event.preventDefault();
        //add todo item
        createTodo({
            title: this.state.title,
            description: this.state.description
        }).then(() => {
            TodoListContainer.refreshData();
        })


        this.setState({
            title: "",
            description: ""
        })
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <input id="title" type="text" value={this.state.title} onChange={this.handleChange} placeholder="Enter Todo Item Title" />
                <input id="description" type="text" value={this.state.description} onChange={this.handleChange} placeholder="Enter Todo Item Description" />
                <input type="submit" value="Add" />
            </form>
        )
    }

}
